import React, { useState, useEffect, useReducer } from 'react';

export default function Exercice() {

    const [count, setCount] = useState(0);

    useEffect(() => {
        document.title = `Vous avez cliqué ${count} fois`;
    });


    const initialState = { count: 0 };
    const [state, dispatch] = useReducer(reducer, initialState);

    function reducer(state: {count: number}, action: {type: string}) {
        switch (action.type) {
            case 'increment':
                return { count: state.count + 1 };
            case 'decrement':
                return { count: state.count - 1 };
            default:
                throw new Error();
        }
    }
    return (
        <>
            <div>
                <h1 className='font-semibold'>Méthode useReducer:</h1>
                <button className='bg-red-400 hover:bg-red-600 text-white font-bold py-1 px-2 rounded mr-4'onClick={() => dispatch({ type: 'decrement' })}>-</button>
                <button className='bg-emerald-400 hover:bg-emerald-600 text-white font-bold py-1 px-2 rounded' onClick={() => dispatch({ type: 'increment' })}>+</button>
                <p className='text-xs font-semibold  py-1 px-2 rounded text-emerald-600 bg-emerald-200'>Total : {state.count}</p>
            </div>
            <br />
            <div>
                <h1>Méthode useEffect:</h1>
                <p>Vous avez cliqué {count} fois</p>
                <button onClick={() => setCount(count + 1)}>
                    Cliquez ici
                </button>
            </div>
        </>)
}
