import NavBar from "../components/UI/NavBar"
import { animal } from '../interfaces/interfaces'
import Animals from '../components/Animals'
import { useEffect, useState } from "react";
import axios, { AxiosResponse } from "axios";


const duranimals: animal[] = [
    {
      animalId: 'e1',
      name: 'Toilet Paper',
    },
    {
      animalId: 'e2',
      name: 'Toilet Paper',
    },
    {
      animalId: 'e3',
      name: 'Toilet Paper',
    },
    {
      animalId: 'e4',
      name: 'Toilet Paper',
    },
];


async function componentDidMount(name: string) {

  // POST request using axios with async/await
  const response = await axios.post('https://localhost:7298/Animal/CreateAnimal', name);
}

export default function Animal() {

  const [respons, setResponse] = useState<animal[]>([])

 useEffect(() => {
  axios.get('https://localhost:7298/Animal/GetAnimals')
  .then((response: AxiosResponse) => {
    const animals: animal[] = response.data.map((responseItem: animal) => {
      return {
        animalId: responseItem.animalId,
        name: responseItem.name
      }
    })
    setResponse(animals)
  })
  .catch(error => {
    console.log(error);
  });

 }, [])


  return (
    <>
      <NavBar></NavBar> 
      <Animals items={respons}></Animals>
      <div>
        <label>Create Animal: </label>
        <input type="text" className="mb-6 bg-gray-100 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 cursor-not-allowed dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-gray-400 dark:focus:ring-blue-500 dark:focus:border-blue-500"/>
        <button onClick={() => {componentDidMount('testt')}}> clique</button>
      </div>
    </>
  );

}
